'// function to get the % completion of fields',
'// @param1 - Region name as string',
'function profile_completion(region_name){',
'    //get various fields in a region by classnames',
'// text field',
'var tf_all = document.getElementById(region_name).getElementsByClassName(''text_field'').length;',
'// number field',
'var nf_all = document.getElementById(region_name).getElementsByClassName(''number_field'').length;',
'// select list',
'var sl_all = document.getElementById(region_name).getElementsByClassName(''selectlist'').length;',
'// date picker',
'var dp_all = document.getElementById(region_name).getElementsByClassName(''datepicker'').length;',
'// date picker',
'var cb_all = document.getElementById(region_name).getElementsByClassName(''simple_checkbox'').length;',
'',
'',
'// pick all field elements',
'var tf_elements = document.getElementById(region_name).getElementsByClassName(''text_field'');',
'var nf_elements = document.getElementById(region_name).getElementsByClassName(''number_field'');',
'var sl_elements = document.getElementById(region_name).getElementsByClassName(''selectlist'');',
'var dp_elements = document.getElementById(region_name).getElementsByClassName(''datepicker'');',
'var cb_elements = document.getElementById(region_name).getElementsByClassName(''simple_checkbox'');',
'',
'',
'// count of disabled elements',
'var tf_disabled = 0;',
'var nf_disabled = 0;',
'var sl_disabled = 0;',
'var dp_disabled = 0;',
'var cb_disabled = 0;',
'',
'// count of applicable fields only',
'var tf_applicable = 0;',
'var nf_applicable = 0;',
'var sl_applicable = 0;',
'var dp_applicable = 0;',
'var cb_applicable = 0;',
'',
'    // total number of applicable fields',
'var applicable_fields = 0;',
'',
'// count of filled fields only',
'var tf_filled = 0;',
'var nf_filled = 0;',
'var sl_filled = 0;',
'var dp_filled = 0;',
'var cb_filled = 0;',
'',
'// total number of applicable fields with value populated',
'var filled_fields = 0;',
'',
'// fields - percent completion',
'',
'var percent_completion = 0;',
'',
'// check if any fields are disabled and if yes, take the count of it',
'for (var tf = 0; tf < tf_all; tf++){',
'    if (tf_elements[tf].disabled!=""){',
'        tf_disabled += 1;',
'    }',
'    if(tf_elements[tf].value!="" && tf_elements[tf].disabled==""){',
'        tf_filled += 1;',
'',
'    }',
'',
'}',
'for (var nf = 0; nf < nf_all; nf++){',
'    if (nf_elements[nf].disabled!=""){',
'        nf_disabled += 1;',
'    }',
'    if(nf_elements[nf].value!="" && nf_elements[nf].disabled==""){',
'        nf_filled += 1; ',
'    }',
'}',
'for (var sl = 0; sl < sl_all; sl++){',
'    if (sl_elements[sl].disabled!=""){',
'        sl_disabled += 1;',
'    }',
'    if(sl_elements[sl].value!="" && sl_elements[sl].disabled==""){',
'        sl_filled += 1;',
'    }',
'}',
'for (var dp = 0; dp < dp_all; dp++){',
'    if (dp_elements[dp].disabled!=""){',
'        dp_disabled += 1;',
'    }',
'    if(dp_elements[dp].value!="" && dp_elements[dp].disabled==""){',
'        dp_filled += 1;',
'    }',
'}',
'for (var cb = 0; cb < cb_all; cb++){',
'    if (cb_elements[cb].disabled!=""){',
'        cb_disabled += 1;',
'    }',
'    if(cb_elements[cb].checked!=false && cb_elements[cb].disabled==""){',
'        cb_filled += 1;',
'    }',
'}',
'',
'tf_applicable = tf_all - tf_disabled;',
'nf_applicable = nf_all - nf_disabled;',
'sl_applicable = sl_all - sl_disabled;',
'dp_applicable = dp_all - dp_disabled;',
'cb_applicable = cb_all - cb_disabled;',
'',
'applicable_fields = tf_applicable + nf_applicable + sl_applicable + dp_applicable + cb_applicable;',
'',
'filled_fields = tf_filled + nf_filled + sl_filled + dp_filled + cb_filled;',
'',
'percent_completion = Math.floor(filled_fields / applicable_fields * 100);',
'',
'',
'console.log("Total applicable fields :" , applicable_fields);',
'console.log("Total filled fields :", filled_fields);',
'console.log("% completion : ", percent_completion);',
'',
'console.log("-----------------");',
'',
'return percent_completion;',
'',
'}',
'',
'document.getElementById(''P14_CUT_PERCENT_COMPLETE'').value = profile_completion(''P14_PLANDCUT'');',
'$("#plandcut").text(profile_completion(''P14_PLANDCUT''));',
